    function balanceOf(address account) external view override returns (uint256) {
        return _balances[account];
    }

    function transfer(address to, uint256 value) external override returns (bool) {
        require(_balances[msg.sender] >= value, unicode"é¤˜é¡ä¸è¶³");
        _balances[msg.sender] -= value;
        _balances[to] += value;
        emit Transfer(msg.sender, to, value);
        return true;
    }

    function allowance(address owner, address spender) external view override returns (uint256) {
        return _allowances[owner][spender];
    }

    function approve(address spender, uint256 value) external override returns (bool) {
        _allowances[msg.sender][spender] = value;
        emit Approval(msg.sender, spender, value);
        return true;
    }

    function transferFrom(address from, address to, uint256 value) external override returns (bool) {
        require(_balances[from] >= value, unicode"é¤˜é¡ä¸è¶³");
        require(_allowances[from][msg.sender] >= value, unicode"è¨±å¯æ¬Šä¸è¶³");
        _balances[from] -= value;
        _balances[to] += value;
        _allowances[from][msg.sender] -= value;
        emit Transfer(from, to, value);
        return true;
    }

    function mint(address account, uint256 value) external override checkOwner {
        require(account != address(0), unicode"addressä¸èƒ½æ˜¯0x0");  // æª¢æŸ¥å¸³æˆ¶ä¸èƒ½æ˜¯0x0
        _totalSupply += value;  // å¢åŠ ç¸½ç™¼è¡Œé‡
