        emit Transfer(address(0), account, value);  // ç„¡ä¸­ç”Ÿæœ‰(address(0)=>account): (address(0), æŒ‡å®šå¸³æˆ¶, å¤šå°‘ä»£å¹£)
    }

    function burn(address account, uint256 value) external override checkOwner {
        uint256 accountBalance = _balances[account];  // ç¢ºèªè³¬æˆ¶é¤˜é¡
        require(account != address(0), unicode"addressä¸èƒ½æ˜¯0x0");  // æª¢æŸ¥å¸³æˆ¶ä¸èƒ½æ˜¯0x0
        require(accountBalance >= value, unicode"é¤˜é¡ä¸è¶³");  // æª¢æŸ¥é¤˜é¡å¤ ä¸å¤ ç‡’æ¯€
        _balances[account] = accountBalance - value;  // æŒ‡å®šè³¬æˆ¶éŠ·æ¯€ä»£å¹£
        _totalSupply = _totalSupply - value;  // ç¸½ç™¼è¡Œé‡ä¹Ÿæœƒè·Ÿè‘—æ¸›å°‘
        emit Transfer(account, address(0), value);  // å›æ­¸è™›ç„¡(account=>address(0)): (æŒ‡å®šè³¬æˆ¶, address(0), å¤šå°‘ä»£å¹£)
    }
}
